function permit(

    address owner,

    address spender,

    uint256 value,

    uint256 deadline,

    uint8 v,

    bytes32 r,

    bytes32 s

) external {

    require(deadline >= block.timestamp, "HEZ::permit: AUTH_EXPIRED");

    bytes32 encodeData = keccak256(

        abi.encode(

            PERMIT_TYPEHASH,

            owner,

            spender,

            value,

            nonces[owner]++,

            deadline

        )

    );

    _validateSignedData(owner, encodeData, v, r, s);

    _approve(owner, spender, value);

function transferWithAuthorization(

    address from,

    address to,

    uint256 value,

    uint256 validAfter,

    uint256 validBefore,

    bytes32 nonce,

    uint8 v,

    bytes32 r,

    bytes32 s

) external {

    require(

        block.timestamp > validAfter,

        "HEZ::transferWithAuthorization: AUTH_NOT_YET_VALID"

    );

    require(

        block.timestamp < validBefore,

        "HEZ::transferWithAuthorization: AUTH_EXPIRED"

    );

    require(

        !authorizationState[from][nonce],

        "HEZ::transferWithAuthorization: AUTH_ALREADY_USED"

    );



    bytes32 encodeData = keccak256(

        abi.encode(

            TRANSFER_WITH_AUTHORIZATION_TYPEHASH,

            from,

            to,

            value,

            validAfter,

            validBefore,

            nonce

        )

    );

    _validateSignedData(from, encodeData, v, r, s);



    authorizationState[from][nonce] = true;

    _transfer(from, to, value);

    emit AuthorizationUsed(from, nonce);

function getChainId() public pure returns (uint256 chainId) {

    assembly {

        chainId := chainid()

    }

function transfer(address to, uint256 value)

    public

    virtual

    override

    returns (bool)

{

    _transfer(msg.sender, to, value);

    return true;

function transferFrom(

    address from,

    address to,

    uint256 value

) public virtual override returns (bool) {

    uint256 fromAllowance = allowance[from][msg.sender];

    if (fromAllowance != uint256(-1)) {

        allowance[from][msg.sender] = fromAllowance.sub(value);

    }

    _transfer(from, to, value);

    return true;

function _validateSignedData(

    address signer,

    bytes32 encodeData,

    uint8 v,

    bytes32 r,

    bytes32 s

) internal view {

    bytes32 domainSeparator = keccak256(

        abi.encode(

            EIP712DOMAIN_HASH,

            NAME_HASH,

            VERSION_HASH,

            getChainId(),

            address(this)

        )

    );



    bytes32 digest = keccak256(

        abi.encodePacked("\x19\x01", domainSeparator, encodeData)

    );

    address recoveredAddress = ecrecover(digest, v, r, s);

    require(

        recoveredAddress != address(0) && recoveredAddress == signer,

        "HEZ::_validateSignedData: INVALID_SIGNATURE"

    );

function _mint(address to, uint256 value) internal {

    totalSupply = totalSupply.add(value);

    balanceOf[to] = balanceOf[to].add(value);

    emit Transfer(address(0), to, value);

function _burn(address from, uint256 value) internal {

    balanceOf[from] = balanceOf[from].sub(value);

    totalSupply = totalSupply.sub(value);

    emit Transfer(from, address(0), value);

function _approve(

    address owner,

    address spender,

    uint256 value

) private {

    allowance[owner][spender] = value;

    emit Approval(owner, spender, value);

function _transfer(

    address from,

    address to,

    uint256 value

) private {

    require(

        to != address(this) && to != address(0),

        "HEZ::_transfer: NOT_VALID_TRANSFER"

    );

    balanceOf[from] = balanceOf[from].sub(value);

    balanceOf[to] = balanceOf[to].add(value);

    emit Transfer(from, to, value);
